import React, { useState, useEffect } from 'react';

const App = () => {
  const [videoStream, setVideoStream] = useState(null);

  useEffect(() => {
    startVideoStream();

    return () => {
      stopVideoStream();
    };
  }, []);

  const startVideoStream = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      setVideoStream(stream);

      setInterval(() => captureAndSendImage(stream), 1000);
    } catch (error) {
      console.error('Error accessing camera:', error);
    }
  };

  const stopVideoStream = () => {
    if (videoStream) {
      videoStream.getTracks().forEach((track) => track.stop());
    }
  };

  const captureAndSendImage = async (stream) => {
    const video = document.createElement('video');
    video.srcObject = stream;

    video.onloadedmetadata = () => {
      const canvas = document.createElement('canvas');
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;

      const context = canvas.getContext('2d');
      context.drawImage(video, 0, 0, canvas.width, canvas.height);

      const imageData = canvas.toDataURL('image/jpeg');

      // Send the image to Streamlit
      Streamlit.setComponentValue(imageData, "capturedImage");
    };
  };

  return (
    <div>
      <h1>Video Stream Capture</h1>
      <video
        autoPlay
        playsInline
        ref={(video) => { if (video) video.srcObject = videoStream; }}
      />
    </div>
  );
};

export default App;
