import React, { ReactNode } from "react";
import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";

interface State {
  isFocused: boolean;
}

class MyComponent extends StreamlitComponentBase<State> {
  public state = { isFocused: false };
  private videoStream: MediaStream | null = null;
  private intervalId: NodeJS.Timeout | null = null;
  private canvasElement: HTMLCanvasElement | null = null;

  public componentDidMount(): void {
    this.startVideoStream();
  }

  public componentWillUnmount(): void {
    this.stopVideoStream();
  }

  private startVideoStream = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      this.videoStream = stream;

      this.intervalId = setInterval(() => this.captureAndSendImage(), 1000);
      this.forceUpdate(); // Trigger a re-render to update the canvas
    } catch (error) {
      console.error('Error accessing camera:', error);
    }
  };

  private stopVideoStream = () => {
    if (this.videoStream) {
      this.videoStream.getTracks().forEach((track) => track.stop());
    }
    if (this.intervalId) {
      clearInterval(this.intervalId);
    }
  };

  private captureAndSendImage = () => {
    if (this.videoElement && this.canvasElement) {
      const context = this.canvasElement.getContext('2d');
      if (context) {
        // Set the canvas dimensions based on video feed
        this.canvasElement.width = this.videoElement.videoWidth;
        this.canvasElement.height = this.videoElement.videoHeight;

        // Draw video frame onto the canvas
        context.drawImage(this.videoElement, 0, 0, this.canvasElement.width, this.canvasElement.height);

        // Convert canvas content to a Blob
        this.canvasElement.toBlob((blob) => {
          if (blob) {
            // Create a data URL for the Blob
            const url = URL.createObjectURL(blob);

            // Log the URL to the console
            console.log(url);

            // Send the URL to Streamlit
            // Streamlit.setComponentValue(url);
          }
        }, 'image/jpeg');
      }
    }
  };


  public render = (): ReactNode => {
    const { theme } = this.props;
    const style: React.CSSProperties = {};

    if (theme) {
      const borderStyling = `1px solid ${
        this.state.isFocused ? theme.primaryColor : 'gray'
      }`;
      style.border = borderStyling;
      style.outline = borderStyling;
    }

    return (
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <canvas
          ref={(canvas) => { this.canvasElement = canvas; }}
          style={{ width: '100%', maxWidth: '5000px', height: 'auto' }}
        />
      </div>
    );
  };
}

export default withStreamlitConnection(MyComponent);
