import React, { useState, useEffect, ReactNode } from "react";
import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";

interface State {
  numClicks: number;
  isFocused: boolean;
}

class MyComponent extends StreamlitComponentBase<State> {
  public state = { numClicks: 0, isFocused: false };
  private videoStream: MediaStream | null = null;
  private intervalId: NodeJS.Timeout | null = null;

  public componentDidMount(): void {
    this.startVideoStream();
  }

  public componentWillUnmount(): void {
    this.stopVideoStream();
  }

  private startVideoStream = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      this.videoStream = stream;

      this.intervalId = setInterval(() => this.captureAndSendImage(stream), 1000);
    } catch (error) {
      console.error('Error accessing camera:', error);
    }
  };

  private stopVideoStream = () => {
    if (this.videoStream) {
      this.videoStream.getTracks().forEach((track) => track.stop());
    }
    if (this.intervalId) {
      clearInterval(this.intervalId);
    }
  };

  private captureAndSendImage = async (stream: MediaStream) => {
    const video = document.createElement('video');
    video.srcObject = stream;

    video.onloadedmetadata = () => {
      const canvas = document.createElement('canvas');
      canvas.width = video.videoWidth || 0;
      canvas.height = video.videoHeight || 0;

      const context = canvas.getContext('2d');
      if (context) {
        context.drawImage(video, 0, 0, canvas.width, canvas.height);

        const imageData = canvas.toDataURL('image/jpeg');

        // Send the image to Streamlit
        Streamlit.setComponentValue(imageData, "capturedImage");
      }
    };
  };

  private onClicked = (): void => {
    this.setState(
      (prevState) => ({ numClicks: prevState.numClicks + 1 }),
      () => Streamlit.setComponentValue(this.state.numClicks)
    );
  };

  private _onFocus = (): void => {
    this.setState({ isFocused: true });
  };

  private _onBlur = (): void => {
    this.setState({ isFocused: false });
  };

  public render = (): ReactNode => {
    const name = this.props.args["name"];
    const { theme } = this.props;
    const style: React.CSSProperties = {};

    if (theme) {
      const borderStyling = `1px solid ${
        this.state.isFocused ? theme.primaryColor : "gray"
      }`;
      style.border = borderStyling;
      style.outline = borderStyling;
    }

    return (
      <div>
        <h1>Video Stream Capture</h1>
        <video
          autoPlay
          playsInline
          ref={(video) => { if (video) video.srcObject = this.videoStream; }}
        />
        <span>
          Hello, {name}! &nbsp;
          <button
            style={style}
            onClick={this.onClicked}
            disabled={this.props.disabled}
            onFocus={this._onFocus}
            onBlur={this._onBlur}
          >
            Click Me!
          </button>
        </span>
      </div>
    );
  };
}

export default withStreamlitConnection(MyComponent);
