import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";
import React, { ReactNode, useEffect } from "react";
import Webcam from "react-webcam";

const WebcamCapture = () => {
  const webcamRef = React.useRef(null);
  const [imageSrc, setImageSrc] = React.useState(undefined);
  const [isRecording, setIsRecording] = React.useState(false);

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "/script/pose.js";
    script.async = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);


  const capture = React.useCallback(async () => {
    const imageSrc = webcamRef.current.getScreenshot();
    const imageData = {
      instanceId: "randomString",
      quaternion: undefined,
      translationVector: undefined, 
      imageSrc: imageSrc,
    };

    try {
      // Fetch quaternion and translation data from the HTML document
      const output = await window.getPoseData();
      console.log(output);
      //imageData.quaternion = Quaternion;
      //imageData.translationVector = translationVector;

      Streamlit.setComponentValue({ imageData });
    } catch (error) {
      console.error("Error fetching pose data:", error);
    }
  }, [webcamRef, setImageSrc]);

  const startRecording = () => {
    window.startRecording();
    setIsRecording(true);
  };

  const stopRecording = () => {
    setIsRecording(false);
  };

  useEffect(() => {
    let captureInterval;

    if (isRecording) {
      captureInterval = setInterval(() => {
        capture();
      }, 1000);
    } else {
      clearInterval(captureInterval);
    }

    return () => {
      clearInterval(captureInterval);
    };
  }, [isRecording, capture]);

  return (
    <>
      <Webcam
        ref={webcamRef}
        forceScreenshotSourceSize
        screenshotFormat="image/jpeg"
        videoConstraints={{
          height: 720,
          width: 1280,
        }}
        height="360"
        width="640"
        audio={true}
        mirrored={true}
        video={isRecording}
      />
      <div>
      <button onClick={() => { isRecording ? stopRecording() : (startRecording()) }}>
        {isRecording ? "Stop Recording" : "Start Recording"}
      </button>
      </div>
      {imageSrc && <img src={imageSrc} alt="captured" />}
    </>
  );
};

class MyComponent extends StreamlitComponentBase {
  state = { isFocused: false };

  onFocus = () => {
    this.setState({ isFocused: true });
  };

  onBlur = () => {
    this.setState({ isFocused: false });
  };

  render = (): ReactNode => {
    const { theme } = this.props;
    const style: React.CSSProperties = {};

    if (theme) {
      const borderStyling = `1px solid ${
        this.state.isFocused ? theme.primaryColor : "gray"
      }`;
      style.border = borderStyling;
      style.outline = borderStyling;
    }

    return (
      <div>
        <WebcamCapture />
      </div>
    );
  };
}

export default withStreamlitConnection(MyComponent);
