import React, { ReactNode } from "react";
import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";

interface State {
  isFocused: boolean;
}

class MyComponent extends StreamlitComponentBase<State> {
  public state = { isFocused: false };
  private videoStream: MediaStream | null = null;
  private intervalId: NodeJS.Timeout | null = null;
  private videoElement: HTMLVideoElement | null = null;

  public componentDidMount(): void {
    this.startVideoStream();
  }

  private startVideoStream = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      this.videoStream = stream;

      this.videoElement = document.createElement('video');
      this.videoElement.srcObject = stream;

      this.videoElement.onloadedmetadata = () => {
        this.intervalId = setInterval(() => this.captureAndSendImage(), 2000);
      };

    } catch (error) {
      console.error('Error accessing camera:', error);
    }
  };

  private captureAndSendImage = () => {
    if (this.videoElement) {
      const canvas = document.createElement('canvas');
      canvas.width = this.videoElement.videoWidth || 0;
      canvas.height = this.videoElement.videoHeight || 0;
      const context = canvas.getContext('2d');
      if (context) {
        context.drawImage(
          this.videoElement,
          0,
          0,
          canvas.width,
          canvas.height
        );

        // Convert canvas content to a Blob
        canvas.toBlob((blob) => {
          if (blob) {
            // Create a data URL for the Blob
            const url = URL.createObjectURL(blob);

            // Log the URL to the console
            console.log(url);

            // Send the URL to Streamlit
            Streamlit.setComponentValue(url);
          }
        }, 'image/jpeg');
      }
    }
  };

  private startRecording = () => {
    if (this.mediaRecorder) {
      this.recordedChunks = [];
      this.mediaRecorder.start();
      this.setState({ isRecording: true });
    }
  };

  private stopRecording = () => {
    if (this.mediaRecorder && this.state.isRecording) {
      this.mediaRecorder.stop();
      this.setState({ isRecording: false });
    }
  };


  public render = (): ReactNode => {
    const { theme } = this.props;
    const style: React.CSSProperties = {};

    if (theme) {
      const borderStyling = `1px solid ${
        this.state.isFocused ? theme.primaryColor : 'gray'
      }`;
      style.border = borderStyling;
      style.outline = borderStyling;
    }

    return (
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', height: '600px' }}>
      {this.videoElement && (
        <video
          autoPlay
          playsInline
          ref={(video) => { if (video) video.srcObject = this.videoStream; }}
          style={{ width: '100%', height: '100%' }}
        />
      )}
      <button onClick={this.startRecording} disabled={this.state.isRecording}>
        Start Recording
      </button>
      <button onClick={this.stopRecording} disabled={!this.state.isRecording}>
        Stop Recording
      </button>
    </div>
    );
  };
}

export default withStreamlitConnection(MyComponent);
